import React, { useState } from 'react';
import './InstructorLogin.css';

const InstructorLogin = () => {
  // State management
  const [currState, setCurrState] = useState("Login"); // Default state is "Login"
  const [showLogin, setShowLogin] = useState(true); // Assuming showLogin controls visibility

  return (
    <div className='login-popup'>
      <form className="login-popup-container">
        <div className="login-popup-title">
          <h2>{currState}</h2>
          <button className="close-button" onClick={handleClose}>X</button>
        </div>
        <div className="login-popup-inputs">
          {currState === "Login" ? null : <input type="text" placeholder='Your name' required />}
          <input type="email" placeholder='Your email' required />
          <input type="password" placeholder='Password' required />
        </div>
        <button>{currState === "Sign Up" ? "Create account" : "Login"}</button>
        <div className="login-popup-condition">
          <input type="checkbox" required />
          <p>By continuing, I agree to terms of use & privacy policy.</p>
        </div>
        {currState === "Login"
          ? <p>Create a new account? <span onClick={() => setCurrState("Sign Up")}>Click here</span></p>
          : <p>Already have an account? <span onClick={() => setCurrState("Login")}>Login here</span></p>
        }
      </form>
    </div>
  );
};

export default InstructorLogin;
